import React, { useState, useContext } from "react";
import Header from "../components/Header";
import { Link } from "react-router-dom";
import Axios from "axios";
import ServerLink from "../ServerLink";
import md5 from "md5";
import swal from "sweetalert";
import { UserContext } from "../context/UserProvider";

const Login = () => {
  const { getLogin } = useContext(UserContext);
  const [nombre, setNombre] = useState("");
  const [password, setPassword] = useState("");
  const [isLogin, setIsLogin] = useState(false);

  const handleNombre = (e) => {
    setNombre(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  const variablesUsuario = () => {
    Axios.post(ServerLink + "/obtenerDatosUsuario", { idUsuario: 1 })
      .then((response) => {
        return response.data;
      })
      .then((response) => {
        setIsLogin(true);
        // if (response.length > 0) {
        //     var respuesta = response[0]
        //     cookies.set('idUsuario', respuesta.idUsuario, { path: '/' })
        //     cookies.set('nombre', respuesta.nombre, { path: '/' })
        //     cookies.set('apellidos', respuesta.apellidos, { path: '/' })
        //     cookies.set('direccion', respuesta.direccion, { path: '/' })
        //     cookies.set('ciudad', respuesta.ciudad, { path: '/' })
        //     cookies.set('provincia', respuesta.provincia, { path: '/' })
        //     cookies.set('codigoPostal', respuesta.codigoPostal, { path: '/' })
        //     cookies.set('email', respuesta.email, { path: '/' })
        //     swal({
        //         title: "Sesión Iniciada",
        //         text: `Bienvenido, ${respuesta.nombre} ${respuesta.apellidos}`,
        //         icon: "success",
        //         button: "Ok!",
        //     }).then(function () {
        //         // window.location.href = 'http://localhost:3000/login'
        //         window.location.href = 'https://proyecto-final-fran-aragon.netlify.app/login'
        //     })
        // }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const login = async () => {
    let user = await getLogin(nombre, md5(password));
    if (user[0]) {
      localStorage.setItem('user', JSON.stringify(user[0]));
      swal({
        title: "Sesión Iniciada",
        text: `Bienvenido, ${user[0].userName}`,
        icon: "success",
        button: "Ok!",
      });
    } else {
      swal({
        title: "Oh! Algo ha fallado.",
        text: "Usuario o Contraseña incorrectos",
        icon: "error",
        button: "Volver",
      });
    }
  };

  return (
    <div className="pageBackground">
      <Header />
      {isLogin ? (
        <h2>Bienvenido</h2>
      ) : (
        <div className="loginForm">
          <div className="loginFormat">
            <p className="mensajeLogin">INICIAR SESIÓN</p>
            <div className="inicioSesion">
              <input
                className="inputLogin"
                type="text"
                placeholder="Usuario"
                name="usuario"
                onChange={handleNombre}
              />
              <input
                className="inputLogin"
                type="password"
                placeholder="Contraseña"
                name="contrasenia"
                onChange={handlePassword}
              />
              <button className="btnLogin" onClick={() => login()}>
                Iniciar Sesión
              </button>
            </div>
            <div className="flecha">
              {/* <picture>
                        <img id='flecha' alt='flecha' src='../images/flecha.svg'></img>
                    </picture> */}
              <Link className="link" to="/">
                Volver a Inicio
              </Link>
            </div>
            {/* <h1>{loginStatus}</h1> */}
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
