import React from "react";
import ServerLink from "../ServerLink";
import Axios from "axios";

const UserContext = React.createContext();

const UserProvider = ({ children }) => {
  const getUser = async () => {
    try {
      const resp = Axios.get(`${ServerLink}/user`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getGeneralClasification = async () => {
    try {
      const resp = await Axios.get(`${ServerLink}/teams`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getWeeklyClasification = async () => {
    try {
      const resp = await Axios.get(`${ServerLink}/league`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getTeam = async (teamId) => {
    try {
      const resp = await Axios.get(`${ServerLink}/team/${teamId}`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getLogin = async (nombreUsuario, contrasenia) => {
    try {
      const resp = await Axios.post(`${ServerLink}/login`, [nombreUsuario, contrasenia]);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const updateLeague = async (posicion1, posicion1PUNTOS) => {
    try {
      const resp = await Axios.put(`${ServerLink}/actualizar`, [posicion1, posicion1PUNTOS]);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  return (
    <UserContext.Provider value={{ getUser, getTeam, getGeneralClasification, getWeeklyClasification, getLogin, updateLeague }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserProvider, UserContext };
