import React, { useContext, useEffect, useState} from "react";
import Header from "../components/Header";
import { UserContext } from "../context/UserProvider";
import Axios  from "axios";
import ServerLink from "../ServerLink";

const Home = () => {
  const { user } = useContext(UserContext);
  const [team, setTeam] = useState(localStorage.getItem('team'));
  const [teams, setTeams] = useState(localStorage.getItem('teams'));


  const getTeams = async () => {
    await  Axios.get(`${ServerLink}/teams`).then((response) => {
        setTeams(response.data);
      });
  }

  useEffect(() => {
    getTeams();
  }, []);

  return (
    <div className="pageBackground">
      <Header />
      <div className="tableContainer">
        <div className="informationTable">
          <h2>DINERO INVERTIDO</h2>
          <div>
            <div className="tableHeader">
              <div className="teamColumn">Equipo</div>
              <div className="moneyColumn">Dinero</div>
            </div>
            <tbody>
              {/* {
                teams?.map((team) => {
                  return <tr className="teamColumn"><td>{team?.name}</td></tr>
                })
              } */}
            </tbody>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
