            </div>
            <div>
              {teams?.map((team) => {
                return (
                  <>
                    <div className="tableBody">
                      <div className="teamColumn">{team?.name}</div>
                      <div className="moneyColumn">{team?.moneySpent}â‚¬</div>
                    </div>
                  </>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
