import React, { useState } from "react";
import Header from "../components/Header";
import { Link } from "react-router-dom";
import Axios from "axios";
import ServerLink from "../ServerLink";

const Login = () => {
  const [nombre, setNombre] = useState("");
  const [password, setPassword] = useState("");

  const handleNombre = (e) => {
    setNombre(e.target.value);
  };

  const handlePassword = (e) => {
    setPassword(e.target.value);
  };

  const loguin = async () => {
    await Axios.post(ServerLink + '/login',
        // await Axios.post('http://localhost:8000/login',
        { nombreUsuario: nombre, contrasenia: md5(password) })
        .then(response => {
            return response.data;
        })
        .then(response => {
            if (response.length > 0) {
                cookies.set('idUsuario', response[0].idUsuario, { path: '/' })
                cookies.set('nombreUsuario', response[0].nombreUsuario, { path: '/' })
                variablesUsuario()
            } else {
                swal({
                    title: "Oh! Algo ha fallado.",
                    text: "Usuario o Contraseña incorrectos",
                    icon: "error",
                    button: "Volver",
                }).then(function () {
                    // window.location.href = 'http://localhost:3000/login'
                    window.location.href = 'https://proyecto-final-fran-aragon.netlify.app/login'
                })
            }
        })
        .catch(error => {
            console.log(error);
        })
}

  return (
    <div className="pageBackground">
      <Header />
      <div className="loginForm">
        <div className="loginFormat">
          <p className="mensajeLogin">INICIAR SESIÓN</p>
          <div className="inicioSesion">
            <input
              className="inputLogin"
              type="text"
              placeholder="Usuario"
              name="usuario"
              onChange={handleNombre}
            />
            <input
              className="inputLogin"
              type="password"
              placeholder="Contraseña"
              name="contrasenia"
              onChange={handlePassword}
            />
            <button className="btnLogin">Iniciar Sesión</button>
          </div>
          <div className="flecha">
            {/* <picture>
                        <img id='flecha' alt='flecha' src='../images/flecha.svg'></img>
                    </picture> */}
            <Link className="link" to="/">
              Volver a Inicio
            </Link>
          </div>
          {/* <h1>{loguinStatus}</h1> */}
        </div>
      </div>
    </div>
  );
};

export default Login;
