import React, { useState, useContext } from "react";
import { sorting } from "../utils/strings";
import { UserContext } from "../context/UserProvider";

const Home = () => {
  const { teams } = useContext(UserContext);
  // const [teams, setTeams] = useState(JSON.parse(localStorage.getItem("teams")));

  return (
    <div className="informationTable">
      <h2>CLASIFICACIÓN GENERAL GASTOS</h2>
      <div>
        <div className="tableHeader">
          <div className="teamColumn">Equipo</div>
          <div className="moneyColumn">Gastos</div>
        </div>
        <div>
          {sorting(teams, "moneySpent")?.map((team) => {
            return (
              <div key={team.id}>
                <div className="tableBody">
                  <div className="teamColumn">{team?.name}</div>
                  <div className="moneyColumn">{team?.moneySpent}€</div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Home;
