  const [generalClasification, setGeneralClasification] = useState([]);
  const routeParams = useParams();

  const fetchData = async () => {
    // GENERAL
    let teamsGeneral = await getGeneralClasification();
    setGeneralClasification(teamsGeneral);
    // EQUIPOS
    let teamData = await getTeam(Number(routeParams["id"]));
    setTeam(teamData);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const retrieveImage = () => {
    switch (Number(routeParams["id"])) {
      case 1:
        return image1;
      case 2:
        return image2;
      case 3:
        return image3;
      case 4:
        return image4;
      case 5:
        return image5;
      case 6:
        return image6;
      case 7:
        return image7;
      case 8:
        return image8;
      default:
        break;
    }
  };

