                    <div>
                      <span className="teamNameCursor" onClick={() => window.location.href = `/Team/${team?.id}`}>{team?.name}</span>
                      <span>{team?.points} puntos</span>
                    </div>
                  </div>
                  <div className="pointColumn">{team?.points} puntos</div>
                </div>
