import React, { useContext, useEffect, useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { UserContext } from "../context/UserProvider";
import { useParams } from "react-router-dom";
import image1 from "../assets/escudos/escudo.png";

const Team = () => {
  const { getGeneralClasification } = useContext(UserContext);
  const routeParams = useParams();

  const retrieveImage = () => {
    console.log(routeParams["id"]);
    switch (routeParams["id"]) {
      case 1:
        console.log('aqui');
        return image1;
      case 2:
        return image1;
      case 3:
        return image1;
      case 4:
        return image1;
      case 5:
        return image1;
      case 6:
        return image1;
      case 7:
        return image1;
      case 8:
        return image1;

      default:
        break;
    }
  };

  return (
    <div className="pageBackground">
      <Header />
      <div className="teamPage">
        <div>
          <img className="imageEscudo" src={retrieveImage()} />
        </div>
        <div className="teamData">
          <h2>LOS CHICLES DE ANCELOTTI</h2>
          <p>150 puntos | 2.5€ invertidos en la liga</p>
          <p className="messageTeamPage">
            WOW!! Mejora un poco campeón, vas a pagar la cena tu solo!
          </p>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Team;
