import React from "react";
import ServerLink from "../ServerLink";
import Axios from "axios";

const UserContext = React.createContext();

const UserProvider = ({ children }) => {
  const getUser = async () => {
    try {
      const resp = Axios.get(`${ServerLink}/user`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getTeams = async () => {
    try {
      const resp = await Axios.get(`${ServerLink}/teams`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getTeam = async (teamId) => {
    try {
      const resp = await Axios.get(`${ServerLink}/team/${teamId}`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  const getLeague = async (teamId) => {
    try {
      const resp = await Axios.get(`${ServerLink}/league`);
      return resp.data;
    } catch (err) {
      console.log(err);
      return [];
    }
  };

  return (
    <UserContext.Provider value={{ getUser, getTeam, getTeams, getLeague }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserProvider, UserContext };
