import React, { useEffect, useState } from "react";
import ServerLink from "../ServerLink";
import Axios from "axios";

const UserContext = React.createContext();

const UserProvider = ({ children }) => {
  const [user, setUser] = useState("");
  const [teams, setTeams] = useState("");
  const [team, setTeam] = useState("");

  const getUser = async () => {
   await  Axios.get(`${ServerLink}/user`).then((response) => {

        setUser(response.data[0]);
        getTeam(response.data[0].favoriteTeam);
      });
  }

  const getTeams = async () => {
    await  Axios.get(`${ServerLink}/teams`).then((response) => {
        setTeams(response.data);
        localStorage.setItem('teams', response.data);
      });
  }

  const getTeam = async (teamId) => {
    await Axios.get(`${ServerLink}/team/${teamId}`).then((response) => {
        localStorage.setItem('team', response.data);
      });
  }

  useEffect(() => {
    getUser();
    getTeams();
  }, []);

  return <UserContext.Provider value={{user, teams, team, setTeams}}>{children}</UserContext.Provider>;
};

export { UserProvider, UserContext };
