import React, { useContext, useEffect, useState } from "react";
import { UserContext } from "../context/UserProvider";

const JornadasClasification = ({ weekly }) => {
  const { getTeam, getWeeklyClasification } = useContext(UserContext);
  const [weekSelected, setWeekSelected] = useState(1);
  const [position7, setPosition7] = useState([]);
  const [position8, setPosition8] = useState([]);
  const [position9, setPosition9] = useState([]);
  const [position10, setPosition10] = useState([]);

  useEffect(async () => {
        // WEEKLY
        let teamsWeekly = await getWeeklyClasification();
        setWeeklyClasification(teamsWeekly);

    setWeekSelected(weekly.length);
  }, [weekly]);

  const fetchTeams = async (week) => {
    console.log(weekSelected, weekly, weekly[weekSelected]);
    setPosition7(getTeam(weekly[weekSelected - 1]?.position7));
    setPosition8(getTeam(weekly[weekSelected - 1]?.position8));
    setPosition9(getTeam(weekly[weekSelected - 1]?.position9));
    setPosition10(getTeam(weekly[weekSelected - 1]?.position10));

    // let weekNumber = (week !== undefined && week !== 0) ? week : weekly.length
    // console.log(week, weekNumber);

    // if(week !== undefined || week !== 0){
    //   setPosition7(getTeam(weekly[week - 1]?.position7));
    //   setPosition8(getTeam(weekly[week - 1]?.position8));
    //   setPosition9(getTeam(weekly[week - 1]?.position9));
    //   setPosition10(getTeam(weekly[week - 1]?.position10));
    // }else{
    //   console.log(weekly.length);
    //   console.log('AQUI');
    //   setPosition7(getTeam(weekly[weekly.length - 1]?.position7));
    //   setPosition8(getTeam(weekly[weekly.length - 1]?.position8));
    //   setPosition9(getTeam(weekly[weekly.length - 1]?.position9));
    //   setPosition10(getTeam(weekly[weekly.length - 1]?.position10));
    // }
  };

  console.log(position7, position8, position9, position10);

  useEffect(() => {
    fetchTeams();
  }, []);

  return (
    <div className="informationTable">
      <h2>CLASIFICACIÓN GASTOS POR JORNADAS</h2>
      <div className="weeklySelector">
        <select onChange={(selection) => fetchTeams(selection.target.value)}>
          {weekly.map((week) => {
            return (
              <option selected={week.id === weekly.length} value={week.id}>
                Jornada {week.id}
              </option>
            );
          })}
        </select>
      </div>
      <div>
        <div className="tableHeader">
          <div className="teamColumn">Puesto</div>
          <div className="moneyColumn">Equipo</div>
        </div>
        <div>
          {/* POSITION 7 */}
          <div key={7}>
            <div className="tableBody">
              {/* <div className="teamColumn">{team?.name}</div> */}
              {/* <div className="moneyColumn">{team?.moneySpent}€</div> */}
            </div>
          </div>
          {/* POSITION 8 */}
          <div key={8}>
            <div className="tableBody">
              {/* <div className="teamColumn">{team?.name}</div>
              <div className="moneyColumn">{team?.moneySpent}€</div> */}
            </div>
          </div>
          {/* POSITION 9 */}
          <div key={9}>
            <div className="tableBody">
              {/* <div className="teamColumn">{team?.name}</div>
              <div className="moneyColumn">{team?.moneySpent}€</div> */}
            </div>
          </div>
          {/* POSITION 10 */}
          <div key={10}>
            <div className="tableBody">
              {/* <div className="teamColumn">{team?.name}</div>
              <div className="moneyColumn">{team?.moneySpent}€</div> */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JornadasClasification;
